import React from 'react';
import { Card, CardImg, CardBody, CardText, CardTitle,Breadcrumb,Row,Col,Label, BreadcrumbItem, Button, Modal, ModalHeader, ModalBody } from 'reactstrap';
import { Control, LocalForm, Errors, controls} from 'react-redux-form';
import {Link} from 'react-router-dom'
import { Component } from 'react';

const required=(val) => val && val.length;

const maxLength=(len)=> (val) => !(val) || (val.length<= len);
const minLength=(len)=> (val) => (val) && (val.length>= len);
const isNumber =(val) => !isNaN(Number(val));

const validEmail =(val) => /^[A-Z0-9._%+--]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(val);

   class commentForm extends Component{
       constructor(props){
           super(props);
           this.state={
               show: false
           }
       }
       toggleopen(){
           this.setState({
               show: !this.state.show
           })
       }
       
       submitHandle=(values)=>{
            this.toggleopen();
            console.log('Current State is: ' + JSON.stringify(values));
       }

       render(){
           return(
            <React.Fragment>
            <Button className="bg-white text-dark" onClick={this.handleToggle}><i className="fa fa-pencil fa-lg"></i>{' '}Submit Comment</Button>
            <Modal isOpen={this.state.show} toggle={this.toggleopen}>
                <ModalHeader toggle={this.toggleopen}>
                    Submit Comment
                </ModalHeader>
                <ModalBody >
                    <LocalForm onSubmit={(values)=>this.submitHandle(values)}>
                    <Row className="form-group">
                    
                        <Col md={12}>
                            <Control.select model=".rating" id="rating" name="rating"
                                className="form-control"
                                 >
                                 <option>1</option>
                                 <option>2</option>
                                 <option>3</option>
                                 <option>4</option>
                                 <option>5</option>
                            </Control.select>
                        </Col>
                    
                    </Row>

                    <Row className="form-group">
                    <Label htmlFor="firstname" md={2}>Your Name</Label>
                    <Col md={10}>
                        <Control.text model=".author" id="author" name="author"
                            placeholder="Your Name"
                            className="form-control"
                            validators={{
                                required, minLength: minLength(3), maxLength: maxLength(15)
                            }}
                             />
                        <Errors
                            className="text-danger"
                            model=".author"
                            show="touched"
                            messages={{
                                required: 'Required',
                                minLength: 'Must be greater than 2 characters',
                                maxLength: 'Must be 15 characters or less'
                                    }}
                                />
                            </Col>
                        </Row>
                            <Row className='form-group'>
                                <Label htmlFor="message" md={2}>Your Feedback</Label>
                                <Col md={10}>
                                    <Control.textarea model=".comment" id="comment" name="comment"
                                        rows="12"
                                        className="form-control"
                                        validators={{
                                            required
                                        }}
                                        />
                                        <Errors
                                        className="text-danger"
                                        model=".comment"
                                        show="touched"
                                        messages={{
                                            required: 'Comment required ',
                                                }}
                                            />
                                </Col>
                                </Row>
                                <Row className="form-group">
                                    <Col >
                                        <Button type="submit" color="primary" >Submit</Button>
                                    </Col>
                                </Row>
                    </LocalForm>
                </ModalBody>
            </Modal>
            </React.Fragment>
           )
       }
   }
    function RenderComments({comments}) {
        if (comments == null) {
            return (<div></div>)
        }
        const cmnts = comments.map(comment => {
            return (
                <li key={comment.id}>
                    <p>{comment.comment}</p>
                    <p>-- {comment.author},
                    &nbsp;
                    {new Intl.DateTimeFormat('en-US', {
                            year: 'numeric',
                            month: 'long',
                            day: '2-digit'
                        }).format(new Date(comment.date))}
                    </p>
                </li>
            )
        })
        return (
            <div className='col-12 col-md-5 m-1'>
                <h4> Comments </h4>
                <ul className='list-unstyled'>
                    {cmnts}
                </ul>
                <div>
                <commentForm />
                </div>

            </div>
        )
    }

    function RenderDish({dish}) {
        if (dish != null) {
            return (
                <div className='col-12 col-md-5 m-1'>
                    <Card>
                        <CardImg width="100%" src={dish.image} alt={dish.name} />
                        <CardBody>
                            <CardTitle>{dish.name}</CardTitle>
                            <CardText>{dish.description}</CardText>
                        </CardBody>
                    </Card>
                </div>
            )
        }
        else {
            return (<div></div>)
        }
    }

    const DishDetail=(props)=> {
        const dish =props.dish
        if (dish == null) {
            return (<div></div>)
        }
        return (
            <div className="container">
            <div className="row">
                <Breadcrumb>

                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>{props.dish.name}</h3>
                    <hr />
                </div>                
            </div>
            <div className="row">
                <div className="col-12 col-md-5 m-1">
                    <RenderDish dish={props.dish} />
                </div>
                <div className="col-12 col-md-5 m-1">
                    <RenderComments comments={props.comments} />
                </div>
            </div>
            </div>
        );
    }

export default DishDetail
