import React from 'react';
import { Card, CardImg, CardBody, CardText, CardTitle,Breadcrumb, BreadcrumbItem, Button, Modal } from 'reactstrap';

import {Link} from 'react-router-dom'
import { Component } from 'react';
   class commentForm extends Component{
       constructor(props){
           super(props);
           this.state={
               show: false
           }
       }
       toggleopen(){
           this.setState({
               show: !this.state.show
           })
       }
       toggleClose(){
           this.setState({
               show: this.state.show
           })
       }
       render(){
           return(
               <div>
               <Button onClick={this.toggleopen}>Submit Comment</Button>
                <Modal show={this.state.show} onHide={this.toggleClose} >
                <Modal.Header closeButton>
                <Modal.Title>Modal heading</Modal.Title>
              </Modal.Header>
              <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={this.toggleClose}>
                  Close
                </Button>
                <Button variant="primary" onClick={this.toggleopen}>
                  Save Changes
                </Button>
              </Modal.Footer>
               </Modal>
                </div> 
           )
       }
   }
    function RenderComments({comments}) {
        if (comments == null) {
            return (<div></div>)
        }
        const cmnts = comments.map(comment => {
            return (
                <li key={comment.id}>
                    <p>{comment.comment}</p>
                    <p>-- {comment.author},
                    &nbsp;
                    {new Intl.DateTimeFormat('en-US', {
                            year: 'numeric',
                            month: 'long',
                            day: '2-digit'
                        }).format(new Date(comment.date))}
                    </p>
                </li>
            )
        })
        return (
            <div className='col-12 col-md-5 m-1'>
                <h4> Comments </h4>
                <ul className='list-unstyled'>
                    {cmnts}
                </ul>
                <div>
                <commentForm></commentForm>
                </div>

            </div>
        )
    }

    function RenderDish({dish}) {
        if (dish != null) {
            return (
                <div className='col-12 col-md-5 m-1'>
                    <Card>
                        <CardImg width="100%" src={dish.image} alt={dish.name} />
                        <CardBody>
                            <CardTitle>{dish.name}</CardTitle>
                            <CardText>{dish.description}</CardText>
                        </CardBody>
                    </Card>
                </div>
            )
        }
        else {
            return (<div></div>)
        }
    }

    const DishDetail=(props)=> {
        const dish =props.dish
        if (dish == null) {
            return (<div></div>)
        }
        return (
            <div className="container">
            <div className="row">
                <Breadcrumb>

                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>{props.dish.name}</h3>
                    <hr />
                </div>                
            </div>
            <div className="row">
                <div className="col-12 col-md-5 m-1">
                    <RenderDish dish={props.dish} />
                </div>
                <div className="col-12 col-md-5 m-1">
                    <RenderComments comments={props.comments} />
                </div>
            </div>
            </div>
        );
    }

export default DishDetail
