import React from 'react';
import { Card, CardImg, CardBody, CardText, CardTitle,Breadcrumb,Row,Col,Label, BreadcrumbItem, Button, Modal, ModalHeader, ModalBody } from 'reactstrap';
import { Control, LocalForm, Errors, controls} from 'react-redux-form';
import {Link} from 'react-router-dom'
import { Component } from 'react';

const required=(val) => val && val.length;

const maxLength=(len)=> (val) => !(val) || (val.length<= len);
const minLength=(len)=> (val) => (val) && (val.length>= len);
const isNumber =(val) => !isNaN(Number(val));

const validEmail =(val) => /^[A-Z0-9._%+--]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(val);

class CommentForm extends Component{

    constructor(props){
        super(props);
        this.state = {
            
            isModalOpen: false
        };
        
        this.toggleModal = this.toggleModal.bind(this);
        //this.handleLogin = this.handleLogin.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        
    }

    handleSubmit(values){
        console.log("Current State is: " + JSON.stringify(values));
        alert("Current State is: " + JSON.stringify(values));
        
    }

    toggleModal(){
        this.setState({
            isModalOpen: !this.state.isModalOpen
        });
    }

    // handleLogin(event){
    //     this.toggleModal();
    //     alert("Username: " + this.username.value + " Password: " + this.password.value + "Remember: " + this.remember.checked);
    //     event.preventDefault();
    // }

    render(){
        return(
        <React.Fragment>
            <div>
                 <Button outline onClick={this.toggleModal}> 
                    <span className="fa fa-pencil fa-lg"></span>Submit Comment
                </Button>
            </div>

            <div className="col-12 col-md-9">
                        <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal}>
                            <ModalHeader toggle={this.toggleModal}>Submit Comment</ModalHeader>
                           
                            <ModalBody>
                                <LocalForm onSubmit={(values) => this.handleSubmit(values)}> 
                                    <Row className="form-group">
                                        <Col md={12}>
                                            <Label htmlFor="rating" >Rating</Label>                                     
                                            <Control.select model=".rating" id="rating" name="rating" className="form-control">
                                                <option>1</option>
                                                <option>2</option>
                                                <option>3</option>
                                                <option>4</option>
                                                <option>5</option>
                                            </Control.select>
                                        </Col>                                        
                                    </Row>
                                    <Row className="form-group">
                                        <Col md={12}>
                                            <Label htmlFor="yourname" >Your Name</Label>                                     
                                            <Control.text model=".yourname" id="yourname" name="yourname" className="form-control"
                                                validators={{
                                                    required, minLength: minLength(3), maxLength: maxLength(15)
                                                }}
                                            />
                                            <Errors className="text-danger" model=".yourname" show="touched"
                                                messages={{
                                                    required: 'Required',
                                                    minLength: 'Must be greater than 2 charactors',
                                                    maxLength: 'Must be 15 charactors or less'
                                                    
                                                }}
                                            />
                                        </Col>                                        
                                    </Row>
                                    <Row className="form-group">
                                        <Col md={12}>
                                            <Label htmlFor="comment" >Comment</Label>                                     
                                            <Control.textarea model=".comment" id="comment" name="comment" className="form-control" rows="6"/>
                                        </Col>                                        
                                    </Row>
 
                                    <Button type="submit" value="submit" color="primary">Submit</Button>
        
                                </LocalForm>
                            </ModalBody>
                        </Modal>
            </div>
        </React.Fragment>
        )
    }

}

    function RenderComments({comments}) {
        if (comments == null) {
            return (<div></div>)
        }
        const cmnts = comments.map(comment => {
            return (
                <li key={comment.id}>
                    <p>{comment.comment}</p>
                    <p>-- {comment.author},
                    &nbsp;
                    {new Intl.DateTimeFormat('en-US', {
                            year: 'numeric',
                            month: 'long',
                            day: '2-digit'
                        }).format(new Date(comment.date))}
                    </p>
                </li>
            )
        })
        return (
            <div className="col-12 col-md-5 m-1">
                       <Card>
                           <CardBody>
                               <CardTitle>Comments</CardTitle>
                               <CardText>
                                               
                               <ul className="list-unstyled">{cmnts }</ul>
                               <CommentForm></CommentForm>

                               </CardText>
                           </CardBody>
                       </Card>
                    </div>
        )
    }

    function RenderDish({dish}) {
        if (dish != null) {
            return (
                <div className='col-12 col-md-5 m-1'>
                    <Card>
                        <CardImg width="100%" src={dish.image} alt={dish.name} />
                        <CardBody>
                            <CardTitle>{dish.name}</CardTitle>
                            <CardText>{dish.description}</CardText>
                        </CardBody>
                    </Card>
                </div>
            )
        }
        else {
            return (<div></div>)
        }
    }

    const DishDetail=(props)=> {
        const dish =props.dish
        if (dish == null) {
            return (<div></div>)
        }
        return (
            <div className="container">
            <div className="row">
                <Breadcrumb>

                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>{props.dish.name}</h3>
                    <hr />
                </div>                
            </div>
            <div className="row">
                <div className="col-12 col-md-5 m-1">
                    <RenderDish dish={props.dish} />
                </div>
                <div className="col-12 col-md-5 m-1">
                    <RenderComments comments={props.comments} />
                </div>
            </div>
            </div>
        );
    }

export default DishDetail
